INSERT INTO `nbw_term_taxonomy` VALUES (1,1,'category','Chuyên mục lưu trữ những bài viết tin tức hay trên website của chúng tôi. Bao gồm các bài viết hướng dẫn sử dụng; các bài viết review sản phẩm; các bài viết kiến thức về các món đồ xách tay chất lượng. Mời bạn đón xem!',0,7),(2,2,'product_type','',0,17),(3,3,'product_type','',0,0),(4,4,'product_type','',0,0),(5,5,'product_type','',0,0),(6,6,'product_visibility','',0,0),(7,7,'product_visibility','',0,0),(8,8,'product_visibility','',0,0),(9,9,'product_visibility','',0,0),(10,10,'product_visibility','',0,0),(11,11,'product_visibility','',0,0),(12,12,'product_visibility','',0,0),(13,13,'product_visibility','',0,0),(14,14,'product_visibility','',0,0),(15,15,'product_cat','<span class=\"_5mfr\"><span class=\"_6qdm\">❣️</span></span>BẠN BAO NHIÊU TUỔI KHÔNG QUAN TRỌNG, QUAN TRỌNG LÀ BẠN TRÔNG BAO NHIÊU TUỔI<span class=\"_5mfr\"><span class=\"_6qdm\">❣️</span></span>\r\n<span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span>Hãy ngừng ngưỡng mộ những cô gái xinh đẹp khi bạn có thể xinh đẹp hơn.\r\n<span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span>Hãy ngừng tự ti về bản thân khi bạn vẫn có thể thay đổi...\r\n<span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span><span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span><span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span>Một bộ trang phục xinh đẹp kèm đôi giày vừa vặn sẽ giúp bạn thêm tự tin hơn để toả sáng. Đừng lo lắng về size vì chúng tôi có hàng ngàn mẫu đồ đẹp đa dạng mọi kích cỡ.\r\n<span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span>Việc của bạn chỉ là chọn mẫu, chọn size việc còn lại Jackilia sẽ giúp bạn. Đặc biệt, Jackilia cò<span class=\"text_exposed_show\">n đang có chương trình ưu đãi cực khủng</span>\r\n\r\n<span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span><span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span><span class=\"_5mfr\"><span class=\"_6qdm\">????</span></span>Nào nào các nàng ơi cơ hội làm đẹp tới rồi nhanh chân lên đi đợi chi!!!!\r\n\r\nĐược làm mẹ là niềm hạnh phúc lớn nhất của người phụ nữ. Và niềm hạnh phúc ấy, còn thể hiện sâu sắc hơn khi được con yêu diện những trang phục “đôi” đồng điệu trong cảm xúc và yêu thương. Đó cũng chính là lý do những shop thời trang mẹ và bé luôn được các mẹ quan tâm.',0,3),(16,16,'nav_menu','',0,26),(17,17,'product_cat','Máy trợ thính (còn gọi là tai nghe trợ thính) là người bạn đồng hành bên cạnh những người kém thính.\r\n\r\nHơn cả một sản phẩm nó còn là món quà chứa đựng tình cảm quý giá mà chúng ta dành tặng cho những người thân yêu trong gia đình.\r\n<blockquote>Hàng Tốt Nhập Khẩu vô cùng trân trọng tình cảm của quý khách hàng</blockquote>\r\nĐồng thời gửi gắm những mẫu thiết bị trợ thính tốt nhất được các chuyên gia tuyển chọn cẩn thận với ước mong mang đến cuộc sống trọn vẹn, chất lượng hơn cho cộng đồng.',0,14),(18,18,'product_cat','',0,7),(19,19,'product_cat','',0,0),(20,20,'product_cat','',0,1),(21,21,'product_cat','',0,2),(22,22,'product_cat','',0,0),(23,23,'product_tag','',0,1),(24,24,'product_tag','',0,2),(25,25,'product_tag','',0,2),(26,26,'product_tag','',0,6),(27,27,'product_tag','',0,2),(28,28,'product_tag','',0,2),(29,29,'product_tag','',0,2),(30,30,'product_tag','',0,5),(31,31,'product_tag','',0,1),(32,32,'post_tag','',0,2),(33,33,'post_tag','',0,1),(34,34,'post_tag','',0,1),(35,35,'post_tag','',0,3),(36,36,'post_tag','',0,1),(37,37,'post_tag','',0,1),(38,38,'post_tag','',0,1),(39,39,'post_tag','',0,1),(40,40,'post_tag','',0,2),(41,41,'post_tag','',0,2);
