INSERT INTO `wp_posts` VALUES (2,1,'2018-03-15 01:56:55','2018-03-15 01:56:55','[ux_slider label=\"Slide PC\" nav_size=\"normal\" nav_style=\"simple\" bullets=\"false\" timer=\"3000\" visibility=\"hide-for-small\"]\n\n[ux_image id=\"791\" image_size=\"original\" height=\"25%\"]\n\n[ux_image id=\"793\" image_size=\"original\" height=\"25%\"]\n\n\n[/ux_slider]\n[ux_slider label=\"Slide mobile\" nav_size=\"normal\" nav_style=\"simple\" bullets=\"false\" timer=\"3000\" visibility=\"show-for-small\"]\n\n[ux_image id=\"1095\" image_size=\"original\"]\n\n[ux_image id=\"1097\" image_size=\"original\"]\n\n\n[/ux_slider]\n[section label=\"Khóa học\" bg_color=\"rgb(242, 242, 242)\" padding=\"57px\" class=\"khoa-hoc-section\"]\n\n[row label=\"Các khóa học\" style=\"small\"]\n\n[col span__sm=\"12\"]\n\n[title style=\"center\" text=\"Dương cầm Music - khơi nguồn đam mê\" tag_name=\"h2\"]\n\n<div class=\"col small-12 large-12\">\n<div class=\"col-inner\">\n<p style=\"text-align: center;\">Dương Cầm Music liên tục tuyển sinh các lớp năng khiếu Piano, Organ, Guitar, trống, sáo và mua bán các loại nhạc cụ:</p>\n</div>\n</div>\n<div class=\"col medium-3 small-12 large-3\">\n<div class=\"col-inner\">\n<div class=\"box has-hoverhas-hover box-text-bottom\">\n<div class=\"box-image\" style=\"text-align: center;\"> </div>\n</div>\n</div>\n</div>\n\n[/col]\n[col span=\"3\" span__sm=\"6\" align=\"center\"]\n\n[ux_image_box img=\"804\" link=\"#\"]\n\n<h3 style=\"text-align: center;\">Lớp đàn Piano</h3>\n<p style=\"text-align: center;\"><span style=\"font-size: 100%;\">Phòng học đạt chuẩn, đầy đủ nhạc cụ học tập, giáo viên giỏi,… chắc chắn sẽ giúp con bạn cảm nhận âm nhạc tốt hơn…</span></p>\n\n[/ux_image_box]\n\n[/col]\n[col span=\"3\" span__sm=\"6\" align=\"center\"]\n\n[ux_image_box img=\"810\" link=\"#\"]\n\n<h3 style=\"text-align: center;\">Lớp đàn Organ</h3>\n<p style=\"text-align: center;\"><span style=\"font-size: 100%;\">Phòng học đạt chuẩn, đầy đủ nhạc cụ học tập, giáo viên giỏi,… chắc chắn sẽ giúp con bạn cảm nhận âm nhạc tốt hơn…</span></p>\n\n[/ux_image_box]\n\n[/col]\n[col span=\"3\" span__sm=\"6\" align=\"center\"]\n\n[ux_image_box img=\"812\" link=\"#\"]\n\n<h3 style=\"text-align: center;\">Lớp đàn Guitar</h3>\n<p style=\"text-align: center;\">Các lớp đàn Guitar liên tục được tuyển sinh, đủ đàn để các em thực hành và rèn luyện. Giáo viên có nhiều kinh nghiệm</p>\n\n[/ux_image_box]\n\n[/col]\n[col span=\"3\" span__sm=\"6\" align=\"center\"]\n\n[ux_image_box img=\"814\" link=\"#\"]\n\n<h3 style=\"text-align: center;\">Mua bán nhạc cụ</h3>\n<p style=\"text-align: center;\">cung cấp sỉ và lẻ các loại nhạc cụ như: Grand Piano, Upright Piano, Đàn Organ, Đàn Guitar, Đàn Violin, và nhiều loại nhạc cụ khác</p>\n\n[/ux_image_box]\n\n[/col]\n[col span__sm=\"12\" align=\"center\"]\n\n[button text=\"+ Đăng ký tư vấn\" letter_case=\"lowercase\" color=\"secondary\" radius=\"99\" link=\"/lien-he/\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Về chúng tôi\" padding=\"45px\" class=\"ve-chung-toi\"]\n\n[row]\n\n[col span=\"8\" span__sm=\"12\"]\n\n<h2 style=\"text-align: left;\"><span style=\"font-size: 100%;\">Giới thiệu Dương Cầm Music</span></h2>\n<p style=\"text-align: left;\"><span style=\"font-size: 100%;\">Tự hào là một trong những trung tâm âm nhạc chất lượng và uy tín tại Thành phố Ninh Bình, Dương Cầm Music đã đón nhận hàng trăm học viên các khóa Piano và Organ về đào tạo và đạt nhiều thành tựu tốt đẹp.</span></p>\n<p>Bên cạnh đó, Dương Cầm Music cũng cung cấp sỉ &amp; lẻ các loại nhạc cụ dân tộc, nhạc cụ hiện đại như: Grand Piano, Piano điện, đàn Organ, đàn Guitar, đàn Ukulele, trống điện tử, trống cơ, sáo,..</p>\n<p>Sự tin tưởng và ủng hộ của quý vị là động lực phát triển của Dương Cầm Music. Chúc cho quý vị phụ huynh và các em nhỏ luôn mạnh khỏe và nhiều niềm vui. Trân trọng./. <span style=\"color: #ff6600;\"><a style=\"color: #ff6600;\" href=\"/gioi-thieu/\">Xem thêm</a></span></p>\n\n[/col]\n[col span=\"4\" span__sm=\"12\"]\n\n[ux_i