kinh-3d/\"]\n\n\n[/col_inner_1]\n\n[/row_inner_1]\n\n[/col_inner]\n\n[/row_inner]\n[row_inner label=\"Quy trình làm việc\" style=\"small\" col_bg=\"rgb(255, 255, 255)\" class=\"quy-trinh-lam-viec\"]\n\n[col_inner span__sm=\"12\"]\n\n[gap height=\"10px\"]\n\n<div class=\"title-quy-trinh\">\n<div class=\"tieu-de\"><span style=\"text-align: center;\">QUY TRÌNH LÀM VIỆC</span></div>\n<div class=\"duong-line\"></div>\n</div>\n[row_inner_1 style=\"small\"]\n\n[col_inner_1 span=\"3\" span__sm=\"6\"]\n\n[featured_box img=\"469\" img_width=\"46\" pos=\"center\"]\n\n<h3 style=\"text-align: center;\">Nhận file thiết kế</h3>\n<p style=\"text-align: center;\"><span style=\"font-size: 85%;\">hoặc nếu khách hàng chưa có file thiết kế, chúng tôi sẽ thiết kế cho quý khách.</span></p>\n\n[/featured_box]\n\n[/col_inner_1]\n[col_inner_1 span=\"3\" span__sm=\"6\"]\n\n[featured_box img=\"476\" img_width=\"46\" pos=\"center\"]\n\n<h3 style=\"text-align: center;\">Nhận tiền cọc</h3>\n<p style=\"text-align: center;\"><span style=\"font-size: 12.24px; line-height: 18.36px;\">Quý khách thanh toán trước ít nhất 50% số tiền đơn hàng để chúng tôi tiến hành in ấn</span></p>\n\n[/featured_box]\n\n[/col_inner_1]\n[col_inner_1 span=\"3\" span__sm=\"6\"]\n\n[featured_box img=\"479\" img_width=\"46\" pos=\"center\"]\n\n<h3 style=\"text-align: center;\">Giao nhận sản phẩm</h3>\n<p style=\"text-align: center;\"><span style=\"font-size: 12.24px; line-height: 18.36px;\">Nhân viên giao sản phẩm in cho quý khách, quý khách thanh toán số tiền còn lại</span></p>\n\n[/featured_box]\n\n[/col_inner_1]\n[col_inner_1 span=\"3\" span__sm=\"6\"]\n\n[featured_box img=\"481\" img_width=\"46\" pos=\"center\"]\n\n<h3 style=\"text-align: center;\">Bảo hành sản phẩm</h3>\n<p style=\"text-align: center;\"><span style=\"font-size: 12.24px; line-height: 18.36px;\">In Phát Tín nhận bảo hành sản phẩm in để quý khách an tâm thi công</span></p>\n\n[/featured_box]\n\n[/col_inner_1]\n\n[/row_inner_1]\n\n[/col_inner]\n\n[/row_inner