INSERT INTO `nt_posts` VALUES (1,2,'2019-03-20 02:42:08','2019-03-20 02:42:08','<!-- wp:paragraph -->\r\n<p>Cảm ơn vì đã sử dụng WordPress. Đây là bài viết đầu tiên của bạn. Sửa hoặc xóa nó, và bắt đầu bài viết của bạn nhé!</p>\r\n<!-- /wp:paragraph -->','Chào tất cả mọi người!','','publish','closed','open','','chao-moi-nguoi','','','2021-05-13 10:58:05','2021-05-13 03:58:05','',0,'http://noithat10.khowebseotop.vn/?p=1',0,'post','',1),(2,2,'2019-03-20 02:42:08','2019-03-20 02:42:08','[ux_slider style=\"shadow\" slide_width=\"1200px\" nav_size=\"normal\" nav_style=\"simple\" bullet_style=\"simple\" visibility=\"hide-for-small\"]\n\n[ux_image id=\"164\" image_size=\"original\" height=\"33%\"]\n\n[ux_image id=\"145\" image_size=\"original\" height=\"33%\"]\n\n[ux_image id=\"159\" image_size=\"original\" height=\"33%\"]\n\n\n[/ux_slider]\n[ux_image id=\"145\" image_size=\"original\" height=\"70%\" visibility=\"show-for-small\"]\n\n[section label=\"Tại sao chọn chúng tôi\" padding=\"40px\" class=\"tai-sao\"]\n\n[row style=\"large\"]\n\n[col span__sm=\"12\"]\n\n[title style=\"center\" text=\"Tại sao bạn nên chọn nội thất mộc style\" tag_name=\"h2\" color=\"rgb(24, 47, 69)\"]\n\n<p style=\"text-align: center;\"><span style=\"font-size: 95%;\">Giữa vô vàn các lựa chọn, chúng tôi rất hy vọng và cám ơn sự lựa chọn của bạn dành cho Nội thất Mộc Style, tiêu chí hoạt động của chúng tôi:</span></p>\n\n[/col]\n[col span=\"3\" span__sm=\"12\"]\n\n[featured_box img=\"189\" img_width=\"122\" pos=\"center\"]\n\n<h3 class=\"title\" style=\"text-align: center;\">Sản phẩm sáng tạo</h3>\n<div class=\"desc\" style=\"text-align: center;\">Không ngừng đổi mới và cải tiến sản phẩm để phục vụ nhu cầu đa dạng của thị trường</div>\n\n[/featured_box]\n\n[/col]\n[col span=\"3\" span__sm=\"12\"]\n\n[featured_box img=\"192\" img_width=\"122\" pos=\"center\"]\n\n<h3 class=\"title\" style=\"text-align: center;\">Giá cả cạnh tranh</h3>\n<div class=\"desc\" style=\"text-align: center;\">Chúng tôi tự tin mang đến cho bạn những sản phẩm tốt với giá cạnh tranh nhất thị trường</div>\n\n[/featured_box]\n\n[/col]\n[col span=\"3\" span__sm=\"12\"]\n\n[featured_box img=\"190\" img_width=\"122\" pos=\"center\"]\n\n<h3 class=\"title\" style=\"text-align: center;\">Tiến độ chính xác</h3>\n<div class=\"desc\" style=\"text-align: center;\">Luôn hoàn thành sản phẩm đúng hẹn với thống nhất của khách hàng, không delay làm trễ tiến độ</div>\n\n[/featured_box]\n\n[/col]\n[col span=\"3\" span__sm=\"12\"]\n\n[featured_box img=\"191\" img_width=\"122\" pos=\"center\"]\n\n<h3 class=\"title\" style=\"text-align: center;\">Phục vụ tận tâm</h3>\n<div class=\"desc\" style=\"text-align: center;\">Chúng tôi phục vụ khách hàng với hết tâm huyết của mình để đưa tới những sản phẩm tốt</div>\n\n[/featured_box]\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Bàn ghế cà phê\" bg_color=\"rgb(236, 236, 236)\" class=\"category-section\"]\n\n[title text=\"Bàn ghế cà phê\" tag_name=\"h2\" color=\"rgb(24, 47, 69)\" link_text=\"+ Xem tất cả\" link=\"/ban-ghe-ca-phe/\"]\n\n[row style=\"small\"]\n\n[col span=\"3\" span__sm=\"12\"]\n\n[ux_image id=\"864\" image_size=\"original\" height=\"116.8%\" image_overlay=\"rgba(0, 0, 0, 0.17)\" image_hover=\"overlay-add\" image_hover_alt=\"zoom\" link=\"/ban-ghe-ca-phe/\" target=\"_blank\"]\n\n[ux_products style=\"normal\" type=\"row\" columns=\"1\" products=\"1\" offset=\"6\" image_height=\"75%\" image_size=\"original\" image_hover=\"zoom\"]\n\n\n[/col]\n[col span=\"9\" span__sm=\"12\"]\n\n[ux_products style=\"normal\" type=\"row\" columns=\"3\" products=\"6\" image_height=\"75%\" image_size=\"original\" image_hover=\"zoom\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Bàn ghế nhà hàng\" bg_color=\"rgb(236, 236, 236)\" class=\"category-section\"]\n\n[title text=\"Bàn ghế nhà hàng\" tag_name=\"h2\" color=\"rgb(24, 47, 69)\" link_text=\"+ Xem tất cả\" link=\"/ban-ghe-nha-hang/\"]\n\n[row style=\"small\"]\n\n[col span=\"3\" span__sm=\"12\"]\n\n[ux_image id=\"873\" image_size=\"original\" height=\"116.8%\" image_overlay=\"rgba(0, 0, 0, 0.19)\" image_hover=\"overlay-add\" image_hover_alt=\"zoom\" link=\"/ban-ghe-ca-phe/\" target=\"_blank\"]\n\n[ux_products style=\"normal\" type=\"row\" columns=\"1\" products=\"1\" offset=\"6\" image_height=\"75%\" image_size=\"original\" image_hover=\"zoom\"]\n\n\n[/col]\n[col span=\"9\" span__sm=\"12\"]\n\n[ux_products style=\"normal\" type=\"row\" columns=\"3\" products=\"6\" order=\"asc\" image_height=\"75%\" image_size=\"original\" image_hover=\"zoom\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Giá kệ trang trí\" bg_color=\"rgb(236, 236, 236)\" class=\"category-section\"]\n\n[title text=\"Giá kệ trang trí\" tag_name=\"h2\" color=\"rgb(24, 47, 69)\" link_text=\"+ Xem tất cả\" link=\"/gia-ke-trang-tri/\"]\n\n[row style=\"small\"]\n\n[col span=\"3\" span__sm=\"12\"]\n\n[ux_image id=\"875\" image_size=\"original\" height=\"116.8%\" image_overlay=\"rgba(0, 0, 0, 0.17)\" image_hover=\"overlay-add\" image_hover_alt=\"zoom\" link=\"/ban-ghe-ca-phe/\" target=\"_blank\"]\n\n[ux_products style=\"normal\" type=\"row\" columns=\"1\" products=\"1\" offset=\"6\" image_height=\"75%\" image_size=\"original\" image_hover=\"zoom\"]\n\n\n[/col]\n[col span=\"9\" span__sm=\"12\"]\n\n[ux_products style=\"normal\" type=\"row\" columns=\"3\" products=\"6\" orderby=\"rand\" order=\"asc\" image_height=\"75%\" image_size=\"original\" image_hover=\"zoom\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Khách hàng nói gì về chúng tôi\"]\n\n[row]\n\n[col span__sm=\"12\"]\n\n[title style=\"center\" text=\"Khách hàng nói gì về Nội thất Mộc Style\" tag_name=\"h2\" color=\"rgb(24, 47, 69)\"]\n\n<p style=\"text-align: center;\"><span style=\"font-size: 95%;\">Mời bạn cùng đọc một số chia sẻ của khách hàng về dịch vụ và sản phẩm của Nội thất Mộc Style,<br />qua đó giúp bạn có cách nhìn khách quan và thiện cảm hơn với công ty chúng tôi:</span></p>\n[gap height=\"21px\"]\n\n[row_inner]\n\n[col_inner span=\"4\" span__sm=\"12\"]\n\n[testimonial image=\"297\" image_width=\"100\" name=\"Trịnh Thu Hương\" company=\"Kế toán\"]\n\n<p><span style=\"font-size: 90%;\">Đến với Nội thất Mộc Style - nhớ mãi trong tim. Tôi rất tâm đắc với mẫu mã, chất lượng cũng như giá cả tại Sơn Kim, tôi rất hạnh phúc vì đã lựa chọn được 1 nơi uy tín, đội ngũ làm việc chuyên nghiệp.</span></p>\n\n[/testimonial]\n\n[/col_inner]\n[col_inner span=\"4\" span__sm=\"12\"]\n\n[testimonial image=\"300\" image_width=\"100\" name=\"Nguyễn Thị Trang\" company=\"Sinh viên\"]\n\n<p><span style=\"font-size: 90%;\">Tôi hoàn toàn tin tưởng khi lựa chọn Nội thất Mộc Style là nhà cung cấp bàn ghế cho nhà hàng lẩu cua Đất Mũi. Hằng luôn đặt hàng ở Mộc Style mỗi có khách mua cần bàn ghế cafe gỗ chân sắt số lượng lớn.</span></p>\n\n[/testimonial]\n\n[/col_inner]\n[col_inner span=\"4\" span__sm=\"12\"]\n\n[testimonial image=\"302\" image_width=\"100\" name=\"Nguyễn Hoài An\" company=\"BigData Co\"]\n\n<p><span style=\"font-size: 90%;\">Bàn ghế văn phòng, bàn ghế cafe, bàn làm việc, bàn phòng khách tại tổ ấm mới tôi đều mua ở Nội Thất Mộc style. Vợ chồng tôi rất hài lòng về dịch vụ và sản phẩm của công ty anh Sơn.</span></p>\n\n[/testimonial]\n\n[/col_inner]\n\n[/row_inner]\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Dự án nổi bật\" bg=\"877\" bg_size=\"original\" bg_color=\"rgb(236, 236, 236)\" bg_overlay=\"rgba(0, 0, 0, 0.32)\" class=\"du-an-noi-bat\"]\n\n[row]\n\n[col span__sm=\"12\"]\n\n[title style=\"center\" text=\"MỘT SỐ DỰ ÁN NỔI BẬT CỦA CHÚNG TÔI\" tag_name=\"h2\" color=\"rgb(255, 255, 255)\"]\n\n<p style=\"text-align: center;\"><span style=\"font-size: 95%; color: #ffffff;\">Dưới dây là tổng hợp một số dự án nổi bật của chúng tôi, bạn có thể tham khảo mẫu mã và phong cách thiết kế tương tự cho dự định của bạn:</span></p>\n[blog_posts style=\"normal\" type=\"row\" columns=\"3\" columns__md=\"1\" cat=\"24\" posts=\"6\" show_date=\"false\" excerpt_length=\"20\" image_height=\"55%\" image_size=\"original\" image_overlay=\"rgba(255, 255, 255, 0.18)\" image_hover=\"overlay-add\" image_hover_alt=\"zoom\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Tin tức\" class=\"tin-tuc\"]\n\n[row]\n\n[col span=\"6\" span__sm=\"12\"]\n\n[title text=\"Bài viết tin tức\" link_text=\"+ Xem tất cả\" link=\"/category/bai-viet/\"]\n\n[blog_posts style=\"vertical\" type=\"row\" col_spacing=\"small\" columns=\"1\" columns__md=\"1\" posts=\"1\" show_date=\"false\" excerpt_length=\"34\" comments=\"false\" image_height=\"71%\" image_width=\"40\" image_size=\"original\" image_overlay=\"rgba(255, 255, 255, 0.19)\" image_hover=\"overlay-add\" image_hover_alt=\"zoom\" text_pos=\"middle\" text_align=\"left\" class=\"blog-post-1\"]\n\n[gap height=\"11px\"]\n\n[blog_posts style=\"normal\" type=\"row\" col_spacing=\"small\" columns=\"3\" columns__md=\"1\" posts=\"3\" offset=\"1\" show_date=\"false\" excerpt=\"false\" image_height=\"63%\" image_size=\"original\" image_overlay=\"rgba(255, 255, 255, 0.17)\" image_hover=\"overlay-add\" image_hover_alt=\"zoom\" text_align=\"left\" text_size=\"small\"]\n\n\n[/col]\n[col span=\"6\" span__sm=\"12\" class=\"cot2\"]\n\n[title text=\"thông tin Khuyến mãi\" link_text=\"+ Xem tất cả\" link=\"/category/khuyen-mai/\"]\n\n[blog_posts style=\"normal\" type=\"row\" columns=\"2\" columns__md=\"1\" posts=\"2\" offset=\"4\" show_date=\"false\" excerpt_length=\"33\" image_height=\"75%\" image_size=\"original\" image_overlay=\"rgba(255, 255, 255, 0.17)\" image_hover=\"overlay-add\" image_hover_alt=\"zoom\" text_align=\"left\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Bản đồ\" padding=\"0px\" video_visibility=\"visible\" class=\"ban-do\"]\n\n[row style=\"collapse\" width=\"full-width\"]\n\n[col span=\"7\" span__sm=\"12\" class=\"cot-ban-do\"]\n\n<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3918.8951647747363!2d106.63615131462117!3d10.819334261359117!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x317529667c77fcdd%3A0x39f60dd29f004faf!2zMi84NSBQaOG6oW0gVsSDbiBC4bqhY2gsIFBoxrDhu51uZyAxNSwgVMOibiBCw6xuaCwgSOG7kyBDaMOtIE1pbmgsIFZp4buHdCBOYW0!5e0!3m2!1svi!2s!4v1568732176587!5m2!1svi!2s\" width=\"100%\" height=\"350\" frameborder=\"0\" style=\"border:0;\" allowfullscreen=\"\"></iframe>\n\n[/col]\n[col span=\"5\" span__sm=\"12\" padding=\"30px 40px 0px 20px\" class=\"cham-ngon-song\"]\n\n<h3>Châm ngôn sống còn của Doanh nghiệp:</h3>\n<p>“Chữ tín Quý hơn Vàng”. Nội thất Mộc Style vô cùng tân trọng sự tín nhiệm của Quý Khách hàng. Chúng tôi đánh cược sự nghiệp của mình trong tay Quý vị bằng chất lượng, sự uy tín, độ chuyên nghiệp và giá cả cạnh tranh hàng đầu. Hãy tới và trải nghiệm!</p>\n<p class=\"ten-ceo\">Nguyễn Thị Thư/ Admin</p>\n\n[/col]\n\n[/row]\n\n[/section]','Trang chủ','','publish','closed','open','','trang-chu','','','2019-09-26 22:31:04','2019-09-26 15:31:04','',0,'http://noithat10.khowebseotop.vn/?page_id=2',0,'page','',0),(3,2,'2019-03-20 02:42:08','2019-03-20 02:42:08','<!-- wp:heading --><h2>Chúng tôi là ai</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Địa chỉ website là: http://noithat10.khowebseotop.vn.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Thông tin cá nhân nào bị thu thập và tại sao thu thập</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Bình luận</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Khi khách truy cập để lại bình luận trên trang web, chúng tôi thu thập dữ liệu được hiển thị trong biểu mẫu bình luận và cũng là địa chỉ IP của người truy cập và chuỗi user agent của người dùng trình duyệt để giúp phát hiện spam</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Một chuỗi ẩn danh được tạo từ địa chỉ email của bạn (còn được gọi là hash) có thể được cung cấp cho dịch vụ Gravatar để xem bạn có đang sử dụng nó hay không. Chính sách bảo mật của dịch vụ Gravatar có tại đây: https://automattic.com/privacy/. Sau khi chấp nhận bình luận của bạn, ảnh tiểu sử của bạn được hiển thị công khai trong ngữ cảnh bình luận của bạn.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Thư viện</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Nếu bạn tải hình ảnh lên trang web, bạn nên tránh tải lên hình ảnh có dữ liệu vị trí được nhúng (EXIF GPS) đi kèm. Khách truy cập vào trang web có thể tải xuống và giải nén bất kỳ dữ liệu vị trí nào từ hình ảnh trên trang web.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Thông tin liên hệ</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Cookies</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Nếu bạn viết bình luận trong website, bạn có thể cung cấp cần nhập tên, email địa chỉ website trong cookie. Các thông tin này nhằm giúp bạn không cần nhập thông tin nhiều lần khi viết bình luận khác. Cookie này sẽ được lưu giữ trong một năm.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Nếu bạn có tài khoản và đăng nhập và website, chúng tôi sẽ thiết lập một cookie tạm thời để xác định nếu trình duyệt cho phép sử dụng cookie. Cookie này không bao gồm thông tin cá nhân và sẽ được gỡ bỏ khi bạn đóng trình duyệt.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Khi bạn đăng nhập, chúng tôi sẽ thiết lập một vài cookie để lưu thông tin đăng nhập và lựa chọn hiển thị. Thông tin đăng nhập gần nhất lưu trong hai ngày, và lựa chọn hiển thị gần nhất lưu trong một năm. Nếu bạn chọn &quot;Nhớ tôi&quot;, thông tin đăng nhập sẽ được lưu trong hai tuần. Nếu bạn thoát tài khoản, thông tin cookie đăng nhập sẽ bị xoá.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Nếu bạn sửa hoặc công bố bài viết, một bản cookie bổ sung sẽ được lưu trong trình duyệt. Cookie này không chứa thông tin cá nhân và chỉ đơn giản bao gồm ID của bài viết bạn đã sửa. Nó tự động hết hạn sau 1 ngày.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Nội dung nhúng từ website khác</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Các bài viết trên trang web này có thể bao gồm nội dung được nhúng (ví dụ: video, hình ảnh, bài viết, v.v.). Nội dung được nhúng từ các trang web khác hoạt động theo cùng một cách chính xác như khi khách truy cập đã truy cập trang web khác.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Những website này có thể thu thập dữ liệu về bạn, sử dụng cookie, nhúng các trình theo dõi của bên thứ ba và giám sát tương tác của bạn với nội dung được nhúng đó, bao gồm theo dõi tương tác của bạn với nội dung được nhúng nếu bạn có tài khoản và đã đăng nhập vào trang web đó.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Phân tích</h3><!-- /wp:heading --><!-- wp:heading --><h2>Chúng tôi chia sẻ dữ liệu của bạn với ai</h2><!-- /wp:heading --><!-- wp:heading --><h2>Dữ liệu của bạn tồn tại bao lâu</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Nếu bạn để lại bình luận, bình luận và siêu dữ liệu của nó sẽ được giữ lại vô thời hạn. Điều này là để chúng tôi có thể tự động nhận ra và chấp nhận bất kỳ bình luận nào thay vì giữ chúng trong khu vực đợi kiểm duyệt.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Đối với người dùng đăng ký trên trang web của chúng tôi (nếu có), chúng tôi cũng lưu trữ thông tin cá nhân mà họ cung cấp trong hồ sơ người dùng của họ. Tất cả người dùng có thể xem, chỉnh sửa hoặc xóa thông tin cá nhân của họ bất kỳ lúc nào (ngoại trừ họ không thể thay đổi tên người dùng của họ). Quản trị viên trang web cũng có thể xem và chỉnh sửa thông tin đó.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Các quyền nào của bạn với dữ liệu của mình</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Nếu bạn có tài khoản trên trang web này hoặc đã để lại nhận xét, bạn có thể yêu cầu nhận tệp xuất dữ liệu cá nhân mà chúng tôi lưu giữ về bạn, bao gồm mọi dữ liệu bạn đã cung cấp cho chúng tôi. Bạn cũng có thể yêu cầu chúng tôi xóa mọi dữ liệu cá nhân mà chúng tôi lưu giữ về bạn. Điều này không bao gồm bất kỳ dữ liệu nào chúng tôi có nghĩa vụ giữ cho các mục đích hành chính, pháp lý hoặc bảo mật.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Các dữ liệu của bạn được gửi tới đâu</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Các bình luận của khách (không phải là thành viên) có thể được kiểm tra thông qua dịch vụ tự động phát hiện spam.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Thông tin liên hệ của bạn</h2><!-- /wp:heading --><!-- wp:heading --><h2>Thông tin bổ sung</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Cách chúng tôi bảo vệ dữ liệu của bạn</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Các quá trình tiết lộ dữ liệu mà chúng tôi thực hiện</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Những bên thứ ba chúng tôi nhận dữ liệu từ đó</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Việc quyết định và/hoặc thu thập thông tin tự động mà chúng tôi áp dụng với dữ liệu người dùng</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Các yêu cầu công bố thông tin được quản lý</h3><!-- /wp:heading -->','Chính sách bảo mật','','draft','closed','open','','chinh-sach-bao-mat','','','2019-03-20 02:42:08','2019-03-20 02:42:08','',0,'http://noithat10.khowebseotop.vn/?page_id=3',0,'page','',0),(7,2,'2019-03-20 09:50:58','2019-03-20 02:50:58','','logo-noi-that-moc-style','','inherit','open','closed','','logo-noi-that-moc-style','','','2019-03-20 09:50:58','2019-03-20 02:50:58','',0,'http://noithat10.khowebseotop.vn/wp-content/uploads/2019/03/logo-noi-that-moc-style.png',0,'attachment','image/png',0),(10,2,'2019-03-20 09:51:11','2019-03-20 02:51:11','http://noithat10.khowebseotop.vn/wp-content/uploads/2019/03/cropped-logo-noi-that-moc-style.png','cropped-logo-noi-that-moc-style.png','','inherit','open','closed','','cropped-logo-noi-that-moc-style-png','','','2019-03-20 09:51:11','2019-03-20 02:51:11','',0,'http://noithat10.khowebseotop.vn/wp-content/uploads/2019/03/cropped-logo-noi-that-moc-style.png',0,'attachment','image/png',0),(44,2,'2019-03-20 10:07:53','2019-03-20 03:07:53','','Cửa hàng','','publish','closed','closed','','mua','','','2019-03-20 10:07:53','2019-03-20 03:07:53','',