INSERT INTO `wp_posts` VALUES (2,2,'2019-05-17 03:34:46','2019-05-17 03:34:46','[ux_slider nav_size=\"normal\" nav_style=\"simple\" bullet_style=\"simple\"]\r\n\r\n[ux_image id=\"831\" image_size=\"original\"]\r\n\r\n[ux_image id=\"832\" image_size=\"original\"]\r\n\r\n[/ux_slider]\r\n[section label=\"Top header\" bg_color=\"rgb(33, 168, 92)\" padding=\"19px\" class=\"top-section\"]\r\n\r\n[row padding=\"0px 0 0px 0px\" class=\"row1\"]\r\n\r\n[col span=\"3\" span__sm=\"12\" color=\"light\"]\r\n\r\n[featured_box img=\"125\" img_width=\"45\" pos=\"left\"]\r\n\r\n<span style=\"font-size: 110%;\">Thu mua phế liệu giá cao nhất Miền Trung</span>\r\n\r\n[/featured_box]\r\n\r\n[/col]\r\n[col span=\"3\" span__sm=\"12\" color=\"light\"]\r\n\r\n[featured_box img=\"140\" img_width=\"45\" pos=\"left\"]\r\n\r\n<span style=\"font-size: 110%;\">Thu mua bất kể số lượng, không ngại xa</span>\r\n\r\n[/featured_box]\r\n\r\n[/col]\r\n[col span=\"3\" span__sm=\"12\" color=\"light\"]\r\n\r\n[featured_box img=\"142\" img_width=\"45\" pos=\"left\"]\r\n\r\n<span style=\"font-size: 110%;\">Thu mua tất cả các ngày trong tuần</span>\r\n\r\n[/featured_box]\r\n\r\n[/col]\r\n[col span=\"3\" span__sm=\"12\" align=\"center\" color=\"light\"]\r\n\r\n[featured_box img=\"143\" img_width=\"45\" pos=\"left\"]\r\n\r\n<span style=\"font-size: 110%;\">Hỗ trợ tư vấn báo giá 24/7 tốt nhất</span>\r\n\r\n[/featured_box]\r\n\r\n[/col]\r\n\r\n[/row]\r\n\r\n[/section]\r\n[section label=\"Thư ngỏ\" class=\"thu-ngo-section\"]\r\n\r\n[row class=\"row2\"]\r\n\r\n[col span__sm=\"12\"]\r\n<div class=\"content_abin\">\r\n<h2 class=\"lined-heading\">Thu Mua Phế Liệu Giá Cao Duy Đức</h2>\r\n<p class=\"slogan_about\" style=\"font-size: 17px; color: #dc631b;\">Chuyên thu mua phế liệu giá cao các loại: sắt, đồng, nhôm, kẽm, phế liệu công trình, nhà máy, xí nghiệp,...số lượng không hạn chế.</p>\r\n\r\n<div class=\"lined-heading2\">\r\n\r\n- Thu mua tất cả các loại phế liệu sắt, đồng phế liệu, nhôm phế liệu, inox phế liệu, sắt 