INSERT INTO `wp_posts` VALUES (2,3,'2018-06-03 17:02:29','2018-06-03 17:02:29','<!-- wp:html -->\n[ux_slider nav_size=\"normal\" nav_style=\"simple\" bullet_style=\"simple\" timer=\"5000\"]\n\n[ux_image id=\"779\" image_size=\"original\"]\n\n[ux_image id=\"782\" image_size=\"original\"]\n\n[ux_image id=\"736\" image_size=\"original\"]\n\n\n[/ux_slider]\n[section label=\"section giới thiệu\" bg_color=\"rgb(255, 255, 255)\" class=\"gioi-thieu-section\"]\n\n[row style=\"small\"]\n\n[col span=\"8\" span__sm=\"12\" class=\"cot8\"]\n\n<p style=\"text-align: center;\"><span style=\"color: #000000; font-size: 150%;\"><span style=\"color: #ef0951;\">Jennie Brows</span> - Chuyên thẩm mỹ sắc đẹp</span></p>\n[row_inner style=\"small\"]\n\n[col_inner span=\"6\" span__sm=\"12\"]\n\n[ux_image id=\"120\" image_size=\"original\"]\n\n\n[/col_inner]\n[col_inner span=\"6\" span__sm=\"12\"]\n\n[gap height=\"6px\"]\n\n<p><span style=\"color: #808080; font-size: 95%;\">Sở hữu đội ngũ kỹ thuật viên nhiều kinh nghiệm, hệ thống công nghệ làm đẹp hiện đại và phong cách phục vụ chuyên nghiệp, Jenniebrows tự hào là hệ thống làm đẹp uy tín tại Việt Nam, mang đến vẻ đẹp tự nhiên cho hàng ngàn khách hàng...</span></p>\n[button text=\"Xem thêm\" letter_case=\"lowercase\" style=\"gloss\" radius=\"99\"]\n\n\n[/col_inner]\n\n[/row_inner]\n[gap height=\"18px\"]\n\n[row_inner col_bg=\"rgb(94, 94, 94)\" padding=\"10px 10px 10px 10px\" class=\"row2\"]\n\n[col_inner span__sm=\"12\" color=\"light\"]\n\n<h3><span style=\"font-size: 90%;\">CÁC DỊCH VỤ CHÍNH</span></h3>\n[gap height=\"6px\"]\n\n[blog_posts style=\"vertical\" type=\"row\" col_spacing=\"small\" columns=\"2\" columns__md=\"1\" ids=\"196,203,623,620\" show_date=\"false\" excerpt_length=\"20\" comments=\"false\" image_height=\"110%\" image_width=\"25\" text_pos=\"middle\" text_align=\"left\"]\n\n\n[/col_inner]\n\n[/row_inner]\n\n[/col]\n[col span=\"4\" span__sm=\"12\" padding=\"10px 10px 10px 10px\" bg_color=\"rgb(44, 44, 44)\" color=\"light\" class=\"cot4\" visibility=\"hide-for-medium\"]\n\n<h3><span style=\"font-size: 90%;\">TIN TỨC - BÀI VIẾT</span></h3>\n[blog_posts style=\"vertical\" type=\"row\" col_spacing=\"small\" columns=\"1\" columns__md=\"1\" cat=\"1\" posts=\"3\" show_date=\"false\" comments=\"false\" image_height=\"110%\" image_width=\"22\" text_align=\"left\" text_size=\"small\"]\n\n[row_inner]\n\n[col_inner span__sm=\"12\" align=\"center\"]\n\n[button text=\"Xem thêm\" letter_case=\"lowercase\" style=\"gloss\" radius=\"99\"]\n\n\n[/col_inner]\n\n[/row_inner]\n[gap height=\"10px\"]\n\n[ux_gallery ids=\"201,657,652\" style=\"normal\" col_spacing=\"xsmall\" columns=\"3\" image_height=\"97%\" image_size=\"original\"]\n\n[gap height=\"11px\"]\n\n\n[/col]\n\n[/row]\n\n[/section]\n[section label=\"Section2\" bg=\"742\" bg_size=\"original\" bg_overlay=\"rgba(34, 34, 34, 0.74)\" class=\"Section2\"]\n\n[row]\n\n[col span=\"6\" span__sm=\"12\"]\n\n[ux_image id=\"785\" image_size=\"original\"]\n\n\n[/col]\n[col span=\"6\" span__sm=\"12\" color=\"light\"]\n\n<p><span style=\"font-size: 100%;\"><span style=\"font-size: 200%; color: #f62f73;\">Jennie Brows</span><br /></span><span style=\"font-size: 140%;\">hãy để chúng tôi làm đẹp cho bạn</span></p>\n<p>Với ước mơ biến cả thế giới trở nên xinh đẹp. Chúng tôi không ngừng học hỏi, sáng tạo và đem đến những gì tiên tiến nhất để phục vụ bạn.</p>\n[row_inner]\n\n[col_inner span=\"5\" span__sm=\"12\"]\n\n[featured_box img=\"771\" img_width=\"70\"]\n\n<p><span style=\"font-size: 120%; color: #ffffff;\">Điêu khắc chân mày</span><br /><span style=\"font-size: 95%; color: #c0c0c0;\">Kỹ thuật Điêu khắc chân mày 3D kết hợp phun shading, ombre</span></p>\n\n[/featured_box]\n\n[/col_inner]\n[col_inner span=\"5\" span__sm=\"12\"]\n\n[featured_box img=\"769\" img_width=\"70\"]\n\n<p><span style=\"font-size: 120%; color: #ffffff;\">Xăm môi thẩm mỹ</span><br /><span style=\"font-size: 90%; color: #c0c0c0;\">Kỹ thuật Phun môi Full Lips ( Aqua Lips), Ombre Líp, candy lip.</sp