hoặc nhận quà từ các tiểu thương tại khu chợ.\r\n<ol start=\"3\">\r\n 	<li>\r\n<h3>Khách sạn 7 sao duy nhất trên thế giới Burj Al Arab.</h3>\r\n</li>\r\n</ol>\r\n</div>'),(1553,325,'_lich_trinh_chi_tiet','field_59f978c52b399'),(1555,325,'_edit_lock','1510643212:1'),(1556,325,'_edit_last','1'),(1559,327,'_sku',''),(1560,327,'_regular_price','4200000'),(1561,327,'_sale_price','3000000'),(1562,327,'_sale_price_dates_from',''),(1563,327,'_sale_price_dates_to',''),(1564,327,'total_sales','0'),(1565,327,'_tax_status','taxable'),(1566,327,'_tax_class',''),(1567,327,'_manage_stock','no'),(1568,327,'_backorders','no'),(1569,327,'_sold_individually','no'),(1570,327,'_weight',''),(1571,327,'_length',''),(1572,327,'_width',''),(1573,327,'_height',''),(1574,327,'_upsell_ids','a:0:{}'),(1575,327,'_crosssell_ids','a:0:{}'),(1576,327,'_purchase_note',''),(1577,327,'_default_attributes','a:0:{}'),(1578,327,'_virtual','no'),(1579,327,'_downloadable','no'),(1580,327,'_product_image_gallery','130,131,81,82,83,84,85,86'),(1581,327,'_download_limit','-1'),(1582,327,'_download_expiry','-1'),(1584,327,'_stock',''),(1585,327,'_stock_status','instock'),(1586,327,'_wc_average_rating','0'),(1587,327,'_wc_rating_count','a:0:{}'),(1588,327,'_wc_review_count','0'),(1589,327,'_downloadable_files','a:0:{}'),(1590,327,'_product_attributes','a:0:{}'),(1591,327,'_product_version','3.5.2'),(1592,327,'_price','3000000'),(1593,327,'wc_productdata_options','a:1:{i:0;a:9:{s:11:\"_bubble_new\";s:0:\"\";s:12:\"_bubble_text\";s:0:\"\";s:17:\"_custom_tab_title\";s:0:\"\";s:11:\"_custom_tab\";s:0:\"\";s:14:\"_product_video\";s:0:\"\";s:19:\"_product_video_size\";s:0:\"\";s:24:\"_product_video_placement\";s:0:\"\";s:12:\"_top_content\";s:0:\"\";s:15:\"_bottom_content\";s:0:\"\";}}'),(1594,327,'_yoast_wpseo_primary_product_cat','27'),(1595,327,'_yoast_wpseo_content_score','60'),(1596,327,'loai_san_pham','Tour du lịch'),(1597,327,'_loai_san_pham','field_59f9714292418'),(1598,327,'thoi_gian','3 ngày2 đêm'),