INSERT INTO `nbw_term_taxonomy` VALUES (1,1,'category','',0,4),(2,2,'category','Chuyên cung cấp dịch vụ thám tử dành cho các đối tượng khách hàng là cá nhân như sau : thám tử điều tra ngoại tình , giám sát con cái, học sinh – sinh viên. , xác minh nhân thân, họ hàng , truy tìm người bỏ trốn, thất lạc , theo dõi giám sát việt kiều, ngoại kiều , điều tra tiền hôn nhân , dịch vụ bảo vệ mật 24/24. , điều tra xác minh số điện thoại, biển số xe bí ẩn, theo dõi giám sát theo yêu cầu . Khách hàng có thể liên hệ qua số Hotline/Zalo:  0907.255.686 | 0981.776.466 để được tư vấn về dịch vụ thám tử cá nhân chuyên nghiệp tại Thư Khôi.',0,8),(3,3,'category','Dịch vụ thám tử doanh nghiệp là dịch vụ thám tử điều tra dành cho khách hàng các các doanh nhân , người điều hành công ty bao gồm các dịch vụ sau : Thám tử điều tra hàng giả, hàng nhái, điều tra đối thủ cạnh tranh , giám sát nhân viên , điều tra trộm cắp nội bộ doanh nghiệp ,thiết bị định vị theo dõi, giám sát ô tô, xe máy , và còn nhiều dịch vụ khác theo yêu cầu của quý . Khách hàng có thể liên hệ với Văn Phòng Thám Tử Thư Khôi theo số Hotline/Zalo: 0907.255.686 | 0981.776.466 để được tư vấn tốt nhất.',0,6),(4,4,'category','Thám Tử Thư Khôi chuyên cung cấp và phân phối các thiết bị - công nghệ hiện đại trong nghề thám tử. Khách hàng có thể liên hệ qua số Hotline/Zalo: 0907.255.686 | 0981.776.466 để được tư vấn về dịch vụ thiết bị thám tử tại Thư Khôi.',0,15),(7,7,'nav_menu','',0,21);
