INSERT INTO `nbw_term_taxonomy` VALUES (1,1,'category','',0,7),(2,2,'category','Việc đến quán cafe ngồi và thưởng thức ly cafe vào buổi sáng là đã trở thành thói quen của nhiều người việt. Quán cafe là nơi các bạn trẻ gặp nhau để trò chuyện, hẹn hò. Chính điều này mà loại hình kinh doanh quán cafe ngày càng phát triển. Ngày càng nhiều quán cafe nên loại hình kinh doanh này có tính cạnh tranh cao. Quán cafe của bạn phải thu hút thật nhiều khách mới có lợi nhuận. Mà việc quán đông hay nhiều khách một phần quết định phụ thuộc vào không gian của quán. Nó có đẹp, có độc đáo, sang trọng hay không.\r\n\r\nBỡi thế người chủ mong muốn có một quán cafe độc lạ, có dấu ấn, phong cách riêng.  Còn người khách thì mong muốn thưởng thức ly cafe trong không gian sang trọng, đọc đáo. Cho nên việc thiết kế quán cafe rất quan trọng trong việc buôn bán của quán.\r\n\r\nNhưng để có được một quán cafe để lại ấn tượng cho khách không phải là điều dễ. Nên bạn hãy để cho công ty XD Đại Thanh được hỗ trợ bạn thiết kế một quán cafe độc đáo. Chúng tôi có rất nhiều năm kinh nghiệm trong lĩnh vực này. Đã thiết kế hàng trăm quán cafe mang đậm dấu ấn và phong cách riêng.',0,6),(3,3,'category','',0,6),(4,4,'category','',0,7),(5,5,'category','',0,0),(6,6,'category','',0,0),(7,7,'nav_menu','',0,9),(8,8,'post_tag','',0,1),(9,9,'post_tag','',0,1);
